"""Print messages on the standard output, to debug and for errors."""


import sys


def black(message: str) -> None:
    print(f"\x1b[90m{message}\x1b[0m")


def red(message: str) -> None:
    print(f"\x1b[91m{message}\x1b[0m")


def green(message: str) -> None:
    print(f"\x1b[92m{message}\x1b[0m")


def yellow(message: str) -> None:
    print(f"\x1b[93m{message}\x1b[0m")


def blue(message: str) -> None:
    print(f"\x1b[94m{message}\x1b[0m")


def purple(message: str) -> None:
    print(f"\x1b[95m{message}\x1b[0m")


def cyan(message: str) -> None:
    print(f"\x1b[96m{message}\x1b[0m")


def white(message: str) -> None:
    print(f"\x1b[97m{message}\x1b[0m")


def bold_black(message: str) -> None:
    print(f"\x1b[1;90m{message}\x1b[0m")


def bold_red(message: str) -> None:
    print(f"\x1b[1;91m{message}\x1b[0m")


def bold_green(message: str) -> None:
    print(f"\x1b[1;92m{message}\x1b[0m")


def bold_yellow(message: str) -> None:
    print(f"\x1b[1;93m{message}\x1b[0m")


def bold_blue(message: str) -> None:
    print(f"\x1b[1;94m{message}\x1b[0m")


def bold_purple(message: str) -> None:
    print(f"\x1b[1;95m{message}\x1b[0m")


def bold_cyan(message: str) -> None:
    print(f"\x1b[1;96m{message}\x1b[0m")


def bold_white(message: str) -> None:
    print(f"\x1b[1;97m{message}\x1b[0m")


def debug(message: str, debug_flag: bool = True) -> None:
    """Print a debug message."""
    if debug_flag:
        print(f"\x1b[34mdebug\x1b[0m: {message}")


def warn(message: str) -> None:
    """Print a warning message."""
    print(f"\x1b[43mwarning\x1b[0m: {message}")


def fail(message: str) -> None:
    """Print a failure message and exit."""
    print(f"\x1b[41merror:\x1b[0m: {message}")
    sys.exit(1)
